	let mut floor: i16 = 0;
	let mut basement_reached = false;
	let mut basement_idx: u16 = 0;
	for c in INPUT.chars() {
		match c {
			'(' => floor += 1,
			')' => floor -= 1,
			_ 	=> panic!()
		}

		if !basement_reached { basement_idx += 1 }
		if floor < 0 { basement_reached = true }
	}

